var User = require('../models/users');

var Tree = require('../models/trees');

var express = require('express');

var router = express.Router();

router.getUsers = function (req, res) {
  res.setHeader('Content-Type', 'application/json');
  User.find(function (err, users) {
    if (err) res.send(err);
    res.send(JSON.stringify(users, null, 5));
  });
};

router.checkOne = function (req, res) {
  res.setHeader('Content-Type', 'application/json');
  User.find({
    "_id": req.params.id
  }, function (err, donation) {
    if (err) res.json({
      message: 'USER NOT Found!',
      errmsg: err
    });else res.send(JSON.stringify(donation, null, 5));
  });
};

router.findOne = function (req, res) {
  res.setHeader('Content-Type', 'application/json');
  var query = req.params.id;
  var queryString = query.toString();
  User.find({
    "userName": {
      $regex: queryString
    }
  }, function (err, user) {
    if (err) res.json({
      message: 'USER NOT FOUND',
      errmsg: err
    });else {
      // res.send(user[userName]);
      if (user.length == 0) res.json({
        message: 'USER NOT FOUND'
      }); // if(user != null)
      else res.json({
          message: 'User has found! Do you want to send a request?',
          data: user
        });
    }
  });
};

router.addUser = function (req, res) {
  res.setHeader('Content-Type', 'application/json');
  var user = new User(); //verify whether the userName and userEmail have existed or not

  User.findOne({
    userEmail: req.body.userEmail
  }, function (err, user) {
    if (err) {
      res.json({
        message: 'ERROR',
        errmsg: err
      });
    } else {
      // if(user.userName != null)
      if (user != null && user.userEmail == req.body.userEmail) res.json({
        message: 'user email already exists'
      });else {
        var user1 = new User();
        user1.userName = req.body.userName;
        user1.userEmail = req.body.userEmail;
        user1.userPassword = req.body.userPassword;
        user1.save(function (err) {
          if (err) res.json({
            message: 'ERROR',
            errmsg: err
          });else res.json({
            message: 'User has registered successfully',
            data: user1
          });
        });
      }
    }
  });
};

router.getCoins = function (req, res) {
  res.setHeader('Content-Type', 'application/json');
  User.findById({
    "_id": req.params.id
  }, {
    "_id": 0,
    "userCoins": 1
  }, function (err, user) {
    if (err) res.json({
      message: 'ERROR',
      errmsg: err
    });else {
      user.save(function (err) {
        if (err) res.json({
          message: 'ERROR'
        });else res.json({
          message: 'your coins: ' + user.toString()
        });
      });
    }
  });
};

router.deleteUser = function (req, res) {
  User.findByIdAndRemove(req.params.id, function (err) {
    if (err) res.json({
      message: 'User NOT DELETED!'
    });else res.json({
      message: 'User Successfully Deleted!'
    });
  });
};

router.putTree = function (req, res) {
  res.setHeader('Content-Type', 'application/json');
  User.findById(req.params.id, function (err, user) {
    if (err) res.json({
      message: 'ERROR',
      errmsg: err
    });else {
      if (user.userCoins < 500) {
        res.json({
          message: 'Sorry, you do not have enough money!'
        });
      } else {
        var count = user.tree.length;
        user.tree[count] = req.body._id;
        user.markModified('tree');
        user.userCoins -= 500;
        user.save(function (err) {
          if (err) res.json({
            message: 'Error',
            errmsg: err
          }); // user.tree.push(req.body._id);

          res.json({
            message: 'Tree Successfully Bought!',
            data: user
          });
        });
      }
    }
  });
};

router.deleteTree = function (req, res) {
  res.setHeader('Content-Type', 'application/json');
  User.findById(req.params.id, function (err, user) {
    if (err) res.json({
      message: 'USER not Found!',
      errmsg: err
    });else {
      for (var i = 0; i < user.tree.length; i++) {
        var count = 0;

        if (user.tree[i] == req.body._id) {
          count = i;
          user.tree.splice(i, 1);
          user.markModified('tree');
          user.userCoins += 300;
          user.save(function (err) {
            if (err) res.json({
              message: 'Tree cannot be deleted',
              errmsg: err
            });
            res.json({
              message: 'Tree Successfully Deleted!',
              data: user
            });
          });
        }

        if (i == user.tree.length - 1 && count == 0) res.json({
          message: 'You have not bought this tree!',
          errmsg: err
        });
      }
    }
  });
};

module.exports = router;
//# sourceMappingURL=data:application/json;charset=utf-8;base64,

