    describe('POST /addTree', () => {
        describe('if the inputs are valid', () => {
            it('should return confirmation and update database ', () => {
                const tree = {
                    treeName: "Cactus",
                    //0 for bush,1 for tree
                    treeType: 1,
                    treePicPath: "",
                    treeDescription: "I am the most dangerous and coolest plant, am I?",
